fun main()=java.io.StreamTokenizer(System.`in`.bufferedReader()).run{fun i():Int{nextToken();return nval.toInt()};val n=i();val pq=java.util.PriorityQueue<Int>();val a=ArrayList<R>(n);repeat(n){a.add(R(i(),i()))};a.sortBy{it.d};repeat(n){val(p,d)=a[it];if(pq.size<d){pq.offer(p)}else{if(pq.peek()<p){pq.poll();pq.offer(p)}}};print(pq.sum())};data class R(val p:Int,val d:Int)